```js
#!/usr/bin/env node
 
const program = require('commander')
const inquirer = require('inquirer')
const _ = require('lodash')
const chalk = require('chalk')
const axios = require('axios')
const fs    = require('fs');

program
  .command('module')
  .alias('m')
  .description('創建新的模塊')
  .option('--name [moduleName]')
  // .option('--sass', '啟用sass')
  // .option('--less', '啟用less')
  .action(option => {
    var config = _.assign({
      moduleName: null,
      description: '',
      sass: false,
      less: false
    }, option)

    var promps = []

    if (!config.moduleName) {
      promps.push({
        type: 'input',
        name: 'moduleName',
        message: '請輸入模塊名稱',
        validate: function(value) {
          if (!value) return '不能為空'
          return true
        }
      })

      promps.push({
        type: 'list',
        name: 'cssSelector',
        message: 'what do u want?',
        choices: [
          {
            name: 'id',
            value: '#'
          },
          {
            name: 'class',
            value: '.'
          }
        ]
      })

      promps.push({
        type: 'comfirm',
        name: 'issues',
        message: 'how many issues do you want to get ? (max < 100, min > 0)',
        validate: function(value) {
          if (!value) return '不能為空'
          return true
        }
      })

      inquirer.prompt(promps).then(function (answers) {
        console.log(chalk.green('收工咯'))
        console.log(chalk.blue('收工咯'))
        console.log(chalk.blue.bgRed('收工咯')) //支持设置背景
        console.log(answers)
        if (answers.issues) {
          ajaxIssues(answers.issues);
        }
      })
    }
  })

program.parse(process.argv)

function ajaxIssues(per_page) {
  axios.get(`https://api.github.com/repos/Shenglian/WORK_TIP/issues?per_page=${per_page}`)
  .then(response => {
    fs.writeFile('issues.json', JSON.stringify(response.data), function (err) {
      err ? console.log(err) : console.log('Write operation complete.');
    });
  })
  .catch(function (error) {
    console.log(error);
  });
}
```

