function setCursor(el, st, end) {
  if (el.setSelectionRange) {
    el.focus();
    el.setSelectionRange(st, end);
  } else {
    // IE
    if (el.createTextRange) {
      range = el.createTextRange();
      range.collapse(true);
      range.moveEnd('character', end);
      range.moveStart('character', st);
      range.select();
    }
  }
}
